{code:java}
db.ddns.aggregate([
    {
        $lookup: {
            from: "device", // The collection to join
            localField: "device_id", // Field from ddns collection
            foreignField: "_id", // Field from device collection
            as: "device_info" // Output array field
        }
    },
    {
        $unwind: "$device_info" // Deconstructs the device_info array
    },
    {
        $match: {
            $expr: {
                $ne: ["$ddns_name", "$device_info.__device_name__"] // Check for mismatch
            }
        }
    },
    {
        $project: {
            _id: 1,
            device_id: 1,
            ddns_name: 1,
            device_name: "$device_info.__device_name__" // Include device name for reference
        }
    }
]);
{code}

